const applyFixes = (db: Database) => {
  let appliedCount = 0
  let skippedCount = 0
  let errorCount = 0

  for (const fix of fixes) {
    try {
      // Get all rows for this word using rowid to distinguish duplicates
      const rows = db.query(`SELECT rowid, Desc FROM ${fix.table} WHERE Word = ?`).all(fix.word) as {
        rowid: number
        Desc: string
      }[]

      if (rows.length === 0) {
        console.error(`❌ Word not found: "${fix.word}"`)
        errorCount++
        continue
      }

      // Find the specific row that contains the expected broken pattern
      // const targetRow = rows.find((r) => r.Desc.includes(fix.expectedWrong))
      const targetRow = rows.find(r => r.Desc === fix.expectedWrong)

      if (!targetRow) {
        console.log(`⏭️  Skipped "${fix.word}": No matching version of this word contains the expected pattern.`)
        rows.forEach(row => {
          console.log(row.rowid)
          console.log(JSON.stringify(row.Desc))
        })
        skippedCount++
        continue
      }

      // Apply the fix
      const newDesc = fix.fixedContent
      // const newDesc = targetRow.Desc.replace(
      //   fix.expectedWrong,
      //   fix.fixedContent,
      // )

      // Update specifically by rowid
      db.run(`UPDATE ${fix.table} SET Desc = ? WHERE rowid = ?`, [newDesc, targetRow.rowid])

      console.log(`✅ Fixed "${fix.word}" (rowid: ${targetRow.rowid}): ${fix.reason}`)
      appliedCount++
    } catch (err) {
      console.error(`❌ Error fixing "${fix.word}":`, err)
      errorCount++
    }
  }

  console.log(`\n📊 Summary:`)
  console.log(`   Applied: ${appliedCount}`)
  console.log(`   Skipped: ${skippedCount}`)
  console.log(`   Errors: ${errorCount}`)
}

const db = new Database(DB_PATH)
try {
  console.log('Starting manual fixes (handling duplicate words)...\n')
  applyFixes(db)
} finally {
  db.close()
  console.log('\nDone.')
}
